import React, { useState, useEffect } from 'react';
import bridge from '@vkontakte/vk-bridge';
import { View, ScreenSpinner, AdaptivityProvider, AppRoot, ConfigProvider, SplitLayout, SplitCol, Epic, useAdaptivityConditionalRender, Tabbar, TabbarItem } from '@vkontakte/vkui';
import '@vkontakte/vkui/dist/vkui.css';
import Home from './panels/Home/Home';
import Persik from './panels/Home/Persik';
import Questions from './panels/Questions/Questions';


const App = () => {
	const [activePanel, setActivePanel] = useState('home');
	const [fetchedUser, setUser] = useState(null);
	const [popout, setPopout] = useState(false);
	const { viewWidth } = useAdaptivityConditionalRender();

	useEffect(() => {
		async function fetchData() {
			const user = await bridge.send('VKWebAppGetUserInfo');
			setUser(user);
			setPopout(null);
		}
		fetchData();
	}, []);

	const go = e => {
		setActivePanel(e.currentTarget.dataset.to);
	};

	return (
		<ConfigProvider>
			<AdaptivityProvider>
				<AppRoot>
					<SplitLayout popout={popout}>
						<SplitCol>
							<View activePanel={activePanel}>
								<Home id='home' fetchedUser={fetchedUser} go={go} />
								<Persik id='persik' go={go} />
								<Questions id='questions' go={go} />
							</View>
						</SplitCol>
						<SplitCol width="100%" maxWidth="560px" stretchedOnMobile autoSpaced>
							<Epic 
								activeStory={activePanel}
								tabbar={
									viewWidth.tabletMinus && (
										<Tabbar className={viewWidth.tabletMinus.className}>
											<TabbarItem 
												onClick={go}
												selected={activeStory === 'home'}
												data-story='home'
											>

											</TabbarItem>
											<TabbarItem 
												onClick={go}
												selected={activeStory === 'questions'}
												data-story='questions'
											>

											</TabbarItem>
											<TabbarItem 
												onClick={go}
												selected={activeStory === 'persik'}
												data-story='persik'
											>

											</TabbarItem>
										</Tabbar>
									)
								}
							>

							</Epic>
						</SplitCol>
					</SplitLayout>
				</AppRoot>
			</AdaptivityProvider>
		</ConfigProvider>
	);
}

export default App;
